mapper.select:
select * from t_blog


        and title like '%${title}%'


        and type_id = #{typeId}

mapper.update:
update t_blog set title = #{title}, content = #{content}, photo = #{photo},
    update_time = #{updateTime}, description = #{description}, type_id = #{typeId}, is_published = #{isPublished}
    where id = #{id}
